import styled from "styled-components";
import React, { useContext, useEffect, useState } from "react";
import { AppContext } from "../context/context";
import { AuthContext } from "../context/AuthContext";
import { Card } from "../components/defaultComponents";
import { StudentGraph, TeacherGraph } from "../components";
import { Form } from "react-bootstrap";
import theme from "../Assets/theme";
import axios from "axios";
import { utils } from "../context";

function Dashboard(props) {
  const user = useContext(AuthContext).authState.userInfo;
  const { setShowHeader, setShowNavbar } = React.useContext(AppContext);
  setShowHeader(true);
  setShowNavbar(true);

  const [data, setData] = useState();
  const [subjectOptions, setSubjectOptions] = useState();
  const [teacherOptions, setTeacherOptions] = useState();

  useEffect(() => {
    getSelectOptions();
  }, []);

  // useEffect(() => {
  //   if(subjectOptions && user.tip === "profesor"){
  //     console.log("ok")
  //   }
  // }, [subjectOptions]);

  function subjectHandler(e) {
    if (e.target.id !== -1) {
      console.log(e.target.id);
    }
  }

  const getSelectOptions = async () => {
    try {
      if (user) {
        let url = `${utils.rootURL}`;

        if (user.tip === "student") {
          url += `/students/${user.id}/subjects`;
        } else if (user.tip === "profesor") {
          url += `/teachers/${user.id}/subjects`;
        }

        const { data } = await axios.get(url);
        setData(data);
        console.log(data);

        const subjectOptions = [];

        data.forEach((item) => {
          subjectOptions.push(item.descriere);
        });
        setSubjectOptions(subjectOptions);
      }
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <Wrapper>
      <GreetingsCard>
        <h1>Buna {user.nume},</h1>
        <h2>Cum te simți astăzi?</h2>
      </GreetingsCard>
      <div className="graphCardsContainer">
        <GraphCard className="align-items-center justify-content-center">
          {/* <TeacherGraph></TeacherGraph> */}
          <StudentGraph></StudentGraph>
        </GraphCard>
        <GraphInputCard className="align-items-center justify-content-center">
          <div className="formTitle">Alege setarile graficului</div>
          <Form.Group>
            {/* <Form.Label>Alege materia</Form.Label> */}
            <Form.Control
              as="select"
              className="Forminput"
              onChange={subjectHandler}
            >
              <option key={-1} id={-1}>
                Selectează materia
              </option>
              {subjectOptions &&
                subjectOptions.length > 0 &&
                subjectOptions.map((item, index) => (
                  <option key={index} id={item.id}>
                    {item}
                  </option>
                ))}
            </Form.Control>
          </Form.Group>
          {user.tip === "profesor" && (
            <Form.Group>
              {/* <Form.Label>Alege grupa</Form.Label> */}
              <Form.Control as="select" className="Forminput">
                <option key={-1}>Selectează grupa</option>
                {teacherOptions &&
                  teacherOptions.length > 0 &&
                  teacherOptions.map((item, index) => (
                    <option key={index}>{item}</option>
                  ))}
              </Form.Control>
            </Form.Group>
          )}
        </GraphInputCard>
      </div>
    </Wrapper>
  );
}
const GraphInputCard = styled(Card)`
  flex-grow: 1;
  // .Forminput {
  //   min-width: 5rem;
  // }
  padding: 5rem;

  .formTitle {
    margin-bottom: 1rem;
    font-size: 1.2rem;
  }
`;
const GraphCard = styled(Card)`
  flex-grow: 2;
  margin-right: 1rem;
`;
const GreetingsCard = styled(Card)`
  width: 100%;
  margin-bottom: 1rem;
  h1 {
    font-weight: normal;
    font-size: 1.5rem;
    margin-bottom: 1rem;
  }

  h2 {
    font-weight: normal;
    font-size: 1rem;
  }
`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: 25vw;
  margin-top: 20vh;
  margin-right: 10vw;
  margin-bottom: 10vh;
  height: 53vh;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
  color: ${theme.mainBlack};
  .graphCardsContainer {
    flex-grow: 1;
    width: 100%;
    display: flex;
    height: auto;
  }
`;

export default Dashboard;
