import { useContext, useState } from "react";
import styled from "styled-components";
import theme from "../Assets/theme";
import { AppContext } from "../context";
import { Card, Button } from "../components/defaultComponents";
import { Formik, useFormik } from "formik";
import * as yup from "yup";
import { Form } from "react-bootstrap";
// import "bootstrap/dist/css/bootstrap.min.css";

const schema = yup.object().shape({
  feedback: yup.string(),
  finalGrade: yup.string().matches(/[0-9]?[0-9]?(\.[0-9][0-9]?)?/),
});

function EvaluateTest() {
  const { setShowNavbar } = useContext(AppContext);
  const [feedback, setFeedback] = useState("");
  console.log("esti in pagina de evaluare boss");
  setShowNavbar(false);
  return (
    <Wrapper>
      <EditorCard></EditorCard>
      <ConsoleCard></ConsoleCard>
      <Formik
        validationSchema={schema}
        onSubmit={(values) => {
          console.log(values);
        }}
        initialValues={{
          email: "",
          password: "",
        }}
      >
        {({
          handleSubmit,
          handleChange,
          handleBlur,
          values,
          touched,
          isValid,
          errors,
        }) => (
          <Form noValidate onSubmit={handleSubmit}>
            <FeedbackCard>
              <Banner>
                <p>Comentarii și feedback</p>
              </Banner>
              <TextArea
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
              ></TextArea>
            </FeedbackCard>
            <GradeCard>
              <span className="automaticGrade">
                <label>Nota automata</label>
                <input type="text" value={"10"} readonly={true}></input>
              </span>
              <span className="finalGrade">
                <label>Nota finala</label>
                <input type="text" value={"10"} readonly={true}></input>
              </span>
            </GradeCard>
            <SubmitBtn>
              <div>Trimite</div>
            </SubmitBtn>
          </Form>
        )}
      </Formik>
    </Wrapper>
  );
}

const EditorCard = styled(Card)`
  // grid-area: B;
  // margin-right: 2em;
  // margin-top: 3em;
  // align-items: streched
  // justify-items : streched;
`;

const ConsoleCard = styled(Card)`
  // grid-area: C;
  // margin-right: 2em;
  // position: relative;
  // display: flex;
  // flex-direction: row;
  // justify-content: space-between;
  // overflow: auto;
  // flex-wrap: wrap;
`;

const FeedbackCard = styled(Card)`
  // grid-area: C;
  // margin-right: 2em;
  // position: relative;
  // display: flex;
  // flex-direction: row;
  // justify-content: space-between;
  // overflow: auto;
  // flex-wrap: wrap;
  padding: 0;
`;

const Banner = styled.div`
  width: 100%;
  background-color: ${theme.Grey1};
  border-radius: 10px 10px 0 0;
  color: ${theme.mainBlack};
`;

const TextArea = styled.textarea`
  border: none;
  resize: none;
  outline: none;
  color: ${theme.mainBlack};
  border-radius: 0 0 10px 10px;
`;

const SubmitBtn = styled(Button)`
  // height: 50px;
  // width: 150px;
  // grid-are: E;
  // justify-self: end;
  // align-self: center;
  // margin-right: 2em;
`;

const GradeCard = styled(Card)`
  // grid-area: C;
  // margin-right: 2em;
  // position: relative;
  // display: flex;
  // flex-direction: row;
  // justify-content: space-between;
  // overflow: auto;
  // flex-wrap: wrap;
  background-color: ${theme.Grey1};
`;

const Wrapper = styled.div`
  display: flex;
  height: 90vh;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
`;

export default EvaluateTest;
