import { Card, Button } from "../components/defaultComponents"
import CodeEditor from "../components/CodeEditor";
import styled from "styled-components";
import React, {useEffect, useState} from "react"
import {AppContext} from "../context/context"

function TakeTest({test}) {  
  const {setShowNavbar} = React.useContext(AppContext);
  setShowNavbar(false);

  const {testQuestions, getTestQuestions, isLoading, aceLanguages, getJudgeAssessment} = React.useContext(AppContext);  
  const [language, setLanguage] = useState("javascript")
  const [codeEditorText, setCodeEditorText] = useState("");
  
  useEffect(() => {
    setLanguage(aceLanguages.find((language => language.id == test.id_limbaj_programare)).name)
    getTestQuestions(test.id);
  console.log(testQuestions)
}, [])

  function executaBtnHandler() {
      console.log("buton apasat")
      // getJudgeAssessment()
  }

  function getCodeEditorTextCallback(text) {
    setCodeEditorText(text);
    console.log("inParent " + codeEditorText);
  }




if(isLoading) {
  return ( <div></div>)
}  
  
  return (
    <div>      
      <Wrapper>
        <ProblemCard>
          
        {testQuestions.map((question) => (
          <h3>{question.descriere}</h3>
        ))}
        </ProblemCard>
        <EditorCard>
          <CodeEditor language = {language} getText={getCodeEditorTextCallback}></CodeEditor>
        </EditorCard>
        <ExecuteBtn primary onClick={executaBtnHandler}>Executa</ExecuteBtn>
        <ConsoleCard>Console {`testul cu codul ${test.id} si limbajul ${language}`}</ConsoleCard>
        <SubmitBtn secondary>Trimite</SubmitBtn>
      </Wrapper>
    </div>
  );
}
const Wrapper = styled.section`
  display: grid;
  height: 100vh;
  grid-template-areas:
    "A    B"
    "A    B"
    "A    B"
    "A    B"
    "A    B"
    "A    B"
    "A    D"
    "A    C"
    "A    C"
    "A    E";
  grid-column-gap: 2em;
`;
const ProblemCard = styled(Card)`
  grid-area: A;
`;
const EditorCard = styled(Card)`
  grid-area: B;
  margin-right: 2em;
  margin-top: 5em;
  align-items: streched
  justify-items : streched;
  
`;
const ConsoleCard = styled(Card)`
  grid-area: C;
  margin-right: 2em;
`;

const ExecuteBtn = styled(Button)`
  height: 50px;
  width: 150px;
  grid-are: D;
  justify-self: start;
  align-self: center;
`;
const SubmitBtn = styled(Button)`
  height: 50px;
  width: 150px;
  grid-are: E;
  justify-self: end;
  align-self: center;
  margin-right: 2em;
`;

export default TakeTest;
