import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import styled from "styled-components";
import { AppContext } from "../context/context";
import { Card } from "../components/defaultComponents";
import { Loader } from "./";
import {
  Route,
  Link,
  useRouteMatch,
  withRouter,
  Redirect,
} from "react-router-dom";
import theme from "../Assets/theme";
import { AuthContext } from "../context/AuthContext";

const rootURL = "http://localhost:5000/api";

const TestCard = (props) => {
  return (
    <StyledCard>
      <div className="cardBanner">Titlu test</div>
      <div className="cardContentContainer">
        <div className="testPropsContainer">
          <div className="testPropContainer">
            <p>Materie</p>
            <div>POO</div>
          </div>
          <div className="testPropContainer">
            <p>Intarziat</p>
            <div>DA</div>
          </div>
          <div className="testPropContainer">
            <p>Nota automata</p>
            <div>9.5</div>
          </div>
        </div>
        <div className="gradeContainer">
          <div className="grade">Nenotat</div>
        </div>
      </div>
    </StyledCard>
  );
};

function TestsGrid(props) {
  const { testInProgress, setTestInProgress } = useContext(AppContext);
  // setShowHeader(true);

  const auth = useContext(AuthContext);
  const { authState } = auth;
  const user = authState.userInfo;

  const { url } = useRouteMatch();

  // const [tests, setTests] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [clickedTest, setClickedTest] = useState();

  // useEffect(() => {
  //   getStudentTests(user);
  // }, []);

  useEffect(() => {
    console.log(props.tests);
  }, [props.tests]);

  // const getStudentTests = async (user) => {
  //   console.log("getting tests from db..");
  //   setIsLoading(true);
  //   const response = await axios
  //     .get(`${rootURL}/students/${user.id}/due`)
  //     .catch((err) => console.log(err));

  //   if (response) {
  //     setTests(response.data);
  //   }
  //   setIsLoading(false);
  // };

  // useEffect(() => {
  //   if (clickedTest) {
  //     setTestInProgress(clickedTest);
  //     console.log(testInProgress);
  //   }
  // }, [clickedTest]);

  // const testClicked = (e) => {
  //   if (testInProgress.id < 0) {
  //     console.log("clicked card with id " + e.target.id);
  //     const clickedTest = tests.find((test) => test.id == e.target.id);
  //     // props.parentCallBack(clickedTest);
  //     setClickedTest(clickedTest);
  //     e.preventDefault();
  //   } else {
  //     console.log("test already in progress");
  //     console.log(testInProgress);
  //     e.preventDefault();
  //   }
  // };

  if (isLoading) {
    return <Loader></Loader>;
  }
  return (
    <>
      {clickedTest && <Redirect to={`${url}/takeTest`} />}
      <Wrapper>
        {props.tests &&
          props.tests.map((test, index) => (
            <TestCard test={test}>{test}</TestCard>
          ))}
      </Wrapper>
    </>
  );
}

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  width: 50%;
  a {
    text-decoration: none;
  }
  position: fixed;
  // // align-items: center;
  // // justify-content: center;
  // font-size: 1rem;
  background: ${theme.mainGrey};
  overflow-y: auto;
`;

const TestsContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-around;
  font-size: 1rem;
`;

const StyledCard = styled(Card)`
  cursor: pointer;
  width: 40vw;
  overflow: auto;
  // height: 10rem;
  margin-top: 1rem;
  margin-bottom: 1rem;
  padding: 0;
  font-size: 1rem;

  .cardBanner {
    height: auto;
    text-align: center;
    background: red;
    color: white;
  }

  .cardContentContainer {
    display: flex;
    justify-content: stretch;
    padding-bottom: 1rem;
    padding-top: 1rem;
  }
  .testPropsContainer {
    display: flex;
    width: auto;
  }

  .testPropContainer {
    margin-left: 2rem;
  }
  .gradeContainer {
    display: flex;
    flex-direction: column;
    margin-left: 2rem;
    width: auto;
  }
`;

export default withRouter(TestsGrid);
