import React, { useContext, useEffect, useRef, useState } from "react";
import { FaCaretDown, FaSignOutAlt } from "react-icons/fa";
import { FaFontAwesome } from "react-icons/fa";
//   import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { AuthContext } from "../context/AuthContext";
import defaultAvatar from "../Assets/images/defaultAvatar.png";
import { Button } from "./defaultComponents";

const DropdownItem = ({ item }) => (
  <Button className="userAvatarBtn" onClick={item.onClick}>
    <FaFontAwesome icon={item.icon} />
    <p className="ml-2">{item.title}</p>
  </Button>
);

const DropdownContent = ({ dropdownItems }) => {
  return (
    <div className="bg-white w-full absolute p-4 shadow-lg rounded-lg mt-2">
      {dropdownItems.map((item, i) => {
        return (
          <div className="mt-1" key={i}>
            <DropdownItem item={item} />
          </div>
        );
      })}
    </div>
  );
};

const AvatarDropdown = () => {
  const node = useRef();
  const auth = useContext(AuthContext);
  const { authState } = auth;
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const dropdownItems = [
    {
      title: "Log Out",
      icon: FaSignOutAlt,
      onClick: auth.logout,
    },
  ];

  const handleClick = (e) => {
    if (!node.current.contains(e.target)) {
      setDropdownOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClick);

    return () => {
      document.removeEventListener("mousedown", handleClick);
    };
  }, []);

  return (
    <div ref={node}>
      <Button
        ref={node}
        className="flex rounded-full items-center py-2 px-3 bg-gradient focus:outline-none shadow-lg"
        onClick={() => setDropdownOpen(!dropdownOpen)}
      >
        <img
          src={defaultAvatar}
          className="rounded-full w-6 border-2 border-white"
          alt="Avatar"
        />
        <div className="px-3">
          <p className="text-white">{authState.userInfo.firstName}</p>
        </div>
        <div className="mr-1 text-white">
          <FaCaretDown></FaCaretDown>
        </div>
      </Button>

      {dropdownOpen && (
        <div className="relative">
          <DropdownContent dropdownItems={dropdownItems} />
        </div>
      )}
    </div>
  );
};

export default AvatarDropdown;
