import { useContext, useState } from "react";
import styled from "styled-components";
import theme from "../Assets/theme";
import { AppContext } from "../context";
import { QuestionSlider, CodeEditor } from "../components";
import { Card, Button } from "../components/defaultComponents";
import { Formik, useFormik } from "formik";
import * as yup from "yup";
import { Form, Col } from "react-bootstrap";
// import "bootstrap/dist/css/bootstrap.min.css";

const schema = yup.object().shape({
  feedback: yup.string(),
  finalGrade: yup
    .string()
    .matches(/^([1-9]{1}(\.\d{1,2})?|10)$/, "Format acceptat: xx.xx")
    .required("Nota finala trebuie compeltata"),
});

function EvaluateTest() {
  const { setShowNavbar } = useContext(AppContext);
  // const [feedback, setFeedback] = useState("");
  console.log("esti in pagina de evaluare boss");
  setShowNavbar(false);
  return (
    <Wrapper>
      <EditorCard>
        <QuestionSlider questions={["ceva", "altceva"]}></QuestionSlider>
        <CodeEditor
          language={"javascript"}
          value={"console.log('test');"}
          defaultCode={"console.log('test');"}
          callback={() => console.log()}
        ></CodeEditor>
      </EditorCard>
      <ConsoleCard>
        <div>
          <p>Output:</p>
          {/* {!isConsoleLoading && stdout && <p>{stdout}</p>}
            {isConsoleLoading && <p>Loading...</p>} */}
        </div>
        <div>
          <p>Test Case Status:</p>
          {/* {!isConsoleLoading && testCaseStatus && <p>{testCaseStatus}</p>}
            {isConsoleLoading && <p>Loading...</p>} */}
        </div>
        {/* {!isConsoleLoading && compileError && (
            <div>
              <div>
                <p>Compile Error:</p>
              </div>
              <p>{compileError}</p>
            </div> */}
      </ConsoleCard>
      <Formik
        validateOnSubmit={true}
        validationSchema={schema}
        onSubmit={(values) => {
          console.log(values);
        }}
        initialValues={{
          finalGrade: "",
          feedback: "",
          automaticGrade: "2",
        }}
      >
        {({
          handleSubmit,
          handleChange,
          handleBlur,
          values,
          touched,
          isValid,
          errors,
        }) => (
          <Form noValidate onSubmit={handleSubmit}>
            <FeedbackCard>
              <Banner>
                <h2>Comentarii și feedback</h2>
              </Banner>
              <TextArea
                name="feedback"
                value={values.feedback}
                onChange={handleChange}
              ></TextArea>
            </FeedbackCard>
            <GradeCard>
              <Form.Row>
                <Form.Label as={Col}>
                  <p>Notă finală</p>
                </Form.Label>
                <Form.Group as={Col}>
                  <Form.Control
                    name="finalGrade"
                    type="text"
                    value={values.finalGrade}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    placeholder="Introdu nota"
                    isValid={touched.finalGrade && !errors.finalGrade}
                    isInvalid={touched.finalGrade && !!errors.finalGrade}
                  />
                  <Form.Control.Feedback type="invalid">
                    <p>{errors.finalGrade}</p>
                  </Form.Control.Feedback>
                </Form.Group>

                <Form.Label as={Col}>
                  <p>Notă automată</p>
                </Form.Label>
                <Form.Group as={Col}>
                  <Form.Control
                    name="automaticGrade"
                    type="text"
                    value={values.automaticGrade}
                    onBlur={handleBlur}
                    onChange={handleChange}
                    readOnly
                  />
                </Form.Group>
              </Form.Row>
            </GradeCard>
            <SubmitBtn>
              <div>Trimite</div>
            </SubmitBtn>
          </Form>
        )}
      </Formik>
    </Wrapper>
  );
}

const EditorCard = styled(Card)`
  grid-area: A;
  margin-left: 1em;
`;

const ConsoleCard = styled(Card)`
  grid-area: B;
  margin-left: 1em;
  margin-top: 0.5em;
  margin-bottom: 0.5em;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  overflow: auto;
  flex-wrap: wrap;
`;

const FeedbackCard = styled(Card)`
  grid-area: C;
  // margin-right: 2em;
  // position: relative;
  // display: flex;
  // flex-direction: row;
  // justify-content: space-between;
  // overflow: auto;
  // flex-wrap: wrap;
  padding: 0;
`;

const Banner = styled.div`
  width: 100%;
  background-color: ${theme.Grey1};
  border-radius: 10px 10px 0 0;
  color: ${theme.mainBlack};
`;

const TextArea = styled.textarea`
  border: none;
  resize: none;
  outline: none;
  color: ${theme.mainBlack};
  border-radius: 0 0 10px 10px;
`;

const SubmitBtn = styled(Button)`
  grid-area: E;
  height: 4rem;
  width: 5rem;

  // justify-self: end;
  // align-self: center;
  // margin-right: 2em;
`;

const GradeCard = styled(Card)`
  grid-area: D;
  // margin-right: 2em;
  // position: relative;
  // display: flex;
  // flex-direction: row;
  // justify-content: space-between;
  // overflow: auto;
  // flex-wrap: wrap;
  background-color: ${theme.Grey1};
`;

const Wrapper = styled.div`
  display: grid;
  height: 100%;
  width: 100%;
  max-height: 900px;

  font-size: 3rem;

  p {
    font-size: 1rem;
  }

  grid-template-columns: repeat(2, 1fr);
  grid-template-rows: repeat(10, 1fr);
  grid-template-areas:
    "A    C"
    "A    C"
    "A    C"
    "A    C"
    "A    C"
    "B    C"
    "B    C"
    "B    D"
    "B    E"
    "B    E";
  grid-column-gap: 2em;
`;

export default EvaluateTest;
