import React, { useEffect } from "react";
import styled from "styled-components";
import { AppContext } from "../context/context";
import {Card} from "../components/defaultComponents"
import {Route, Link, useRouteMatch, withRouter} from "react-router-dom"
import { TakeTest } from ".";

function Tests() {
  const {tests, isLoading, getStudentTests} = React.useContext(AppContext);
  const {url} = useRouteMatch()
 
  useEffect(() => {
    getStudentTests();
    console.log(tests);
}, [])

  const testClicked = (e) => {
    console.log(url);
    <Route path={`${url}/takeTest`} component={TakeTest}/>
  }

  if(isLoading) {
    return ( <Wrapper>
      {" "}
      <div>
        <h1>Tests are loading..</h1>
      </div>
    </Wrapper>)
  }  
  return (
    <Wrapper>
    {" "}
    <div>
      <h1>Test programate</h1>
      <TestsContainer>
      {tests.map ((test) => (
          <Link to={`${url}/takeTest`}>
          <StyledCard key = {test.id} id = {test.id} onClick={testClicked}>
            {test.titlu}
          </StyledCard>
          </Link>
      ))}
      </TestsContainer>
      
    </div>
  </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  height: 90vh;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
`;

const TestsContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-around; 
  font-size: 1rem;
`;

const StyledCard = styled(Card)`
    height: 20vh;
    width: 20vw;
    margin: 10%;
    cursor: pointer
`;

export default withRouter(Tests);
