import { useContext, useEffect, useState } from "react";
import {
  Route,
  Switch,
  withRouter,
  Redirect,
  useRouteMatch,
} from "react-router-dom";
import { TakeTest } from ".";
import TestsGrid from "../components/testsGrid";
import { AppContext } from "../context/context";

function Tests() {
  const [test, setTest] = useState();
  const { url } = useRouteMatch();
  // const {setShowNavbar} = useContext(AppContext)
  // setShowNavbar(true);

  const handleCallback = (childData) => {
    // console.log(childData);
    setTest(childData);
  };

  return (
    <>
      <Switch>
        {test && (
          <Route path={`${url}/takeTest`}>
            <TakeTest></TakeTest>
          </Route>
        )}
        <Route
          path="/tests"
          render={(props) => (
            <TestsGrid {...props} parentCallBack={handleCallback} />
          )}
        />
      </Switch>
    </>
  );
}

export default withRouter(Tests);
