import React, { useEffect } from "react";
import styled from "styled-components";
import { AppContext } from "../context/context";
import {Card} from "../components/defaultComponents"


function Tests() {
  const {tests, isLoading, getStudentTests} = React.useContext(AppContext);
  console.log(isLoading)   
 
  useEffect(() => {
    
    console.log(tests);
  })

  const handleSubmit = (e) => {
    e.preventDefault();
    getStudentTests();
  }

  const teste = [{titlu : "testu1"}, {titlu : "testu2"}];
  if(isLoading) {
    return ( <Wrapper>
      {" "}
      <div>
        <h1>Tests are loading..</h1>
      </div>
    </Wrapper>)
  }  
  return (
    <Wrapper>
    {" "}
    <div>
      <h1>Test programate</h1>
      <button onClick={handleSubmit}></button>
      {teste.map (test => {
        return <Card>{test.titlu}</Card>
      })}
    </div>
  </Wrapper>
  );
}

const Wrapper = styled.div`
  display: flex;
  height: 90vh;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
`;

export default Tests;
