import { useContext, useEffect, useState } from "react";
import {
  Route,
  Switch,
  withRouter,
  Redirect,
  useRouteMatch,
  useLocation,
} from "react-router-dom";
import axios from "axios";
import { TakeTest } from ".";
import { TestsGrid, TestsTable, Loader } from "../components/";
import { AppContext, AuthContext, utils } from "../context";

const rootURL = utils.rootURL;

function Tests() {
  const user = useContext(AuthContext).authState.userInfo;

  // const { authState } = auth;
  // const user = authState.userInfo;
  const [tests, setTests] = useState();
  const [isLoading, setIsLoading] = useState(false);
  const { path } = useRouteMatch();
  const { testInProgress, setTestInProgress } = useContext(AppContext);

  // const { setShowNavbar } = useContext(AppContext);
  // setShowNavbar(false);

  // const handleCallback = (childData) => {

  //   setTestInProgress(childData);
  // };
  // useEffect(() => {
  //   console.log(testInProgress);
  // }, [testInProgress]);

  useEffect(() => {
    getStudentDueTests(user, setIsLoading, setTests);
  }, []);

  return (
    <>
      {isLoading ? (
        <Loader></Loader>
      ) : (
        <TestsTable title="Teste Programate" data={tests}></TestsTable>
      )}

      {/* <Switch>
        <Route path={`/tests/takeTest`}>
          <TakeTest></TakeTest>
        </Route>

        <Route
          path="/tests"
          // exact={true}
          render={(props) => (
            <TestsGrid {...props} parentCallBack={handleCallback} />
          )}
        />
      </Switch> */}
    </>
  );
}

const getStudentDueTests = async (user, setIsLoading, setTests) => {
  console.log("getting tests from db..");

  setIsLoading(true);
  const response = await axios
    .get(`${rootURL}/students/${user.id}/due`)
    .catch((err) => console.log(err));

  if (response) {
    setTests(response.data);
  }
  setIsLoading(false);
};

export default Tests;
