import { useContext, useEffect, useState } from "react";
import {
  Route,
  Switch,
  withRouter,
  Redirect,
  useRouteMatch,
  useLocation,
} from "react-router-dom";
import { TakeTest } from ".";
import TestsGrid from "../components/testsGrid";
import { AppContext } from "../context/context";

function Tests() {
  const [test, setTest] = useState();
  const { path } = useRouteMatch();
  const { testInProgress, setTestInProgress } = useContext(AppContext);

  // const {setShowNavbar} = useContext(AppContext)
  // setShowNavbar(true);
  // console.log(path);
  const handleCallback = (childData) => {
    // console.log(childData);
    setTestInProgress(childData);
  };
  useEffect(() => {
    console.log(testInProgress);
  }, [testInProgress]);

  return (
    <>
      {/* <TestsGrid /> */}
      <Switch>
        <Route path={`/tests/takeTest`}>
          <TakeTest></TakeTest>
        </Route>

        <Route
          path="/tests"
          // exact={true}
          render={(props) => (
            <TestsGrid {...props} parentCallBack={handleCallback} />
          )}
        />
      </Switch>
    </>
  );
}

export default Tests;
