import { Link } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { AppContext } from "../context/context";
import { Card, Button } from "../components/defaultComponents";
import styled from "styled-components";
import { Form, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import theme from "../Assets/theme";
import { Formik, useFormik } from "formik";
import * as yup from "yup";
import axios from "axios";
const rootURL = "http://localhost:5000/api";

function Login() {
  const { setShowNavbar, setShowHeader } = React.useContext(AppContext);
  setShowNavbar(false);
  setShowHeader(false);
  const [type, setType] = useState("student");

  const schema = yup.object().shape({
    email: yup
      .string()
      .matches(
        type === "student" ? /(@stud.ase.ro)/ : /(@ie.ase.ro)/,
        "Adresă nepotrivită pentru tipul de utilizator",
        {
          excludeEmptyString: true,
        }
      )
      .email("Adresă de email invalidă")
      .required("Nicio adresă de email"),
    password: yup.string().required("Nicio parolă introdusă"),
  });

  async function login(user) {
    const response = await axios
      .get(`${rootURL}/${user.type}s/login`, {
        email: user.email,
        password: user.password,
      })
      .catch((err) => console.log(err));
    if (response) {
      console.log(response.status);
      console.log(response.data);
    }
  }

  function submitHandler(user) {
    console.log("submitted");
    login(user);
  }

  return (
    <Wrapper>
      <FormCard>
        <Formik
          validationSchema={schema}
          // onSubmit={(values) => {
          //   alert(JSON.stringify(values, null, 2));
          // }}
          onSubmit={(values) => {
            submitHandler(values);
          }}
          initialValues={{
            email: "",
            password: "",
            type: type,
          }}
        >
          {({
            handleSubmit,
            handleChange,
            handleBlur,
            values,
            touched,
            isValid,
            errors,
          }) => (
            <Form noValidate onSubmit={handleSubmit}>
              <Form.Group controlId="formBasicEmail">
                <Form.Label>Adresă de email</Form.Label>
                <Form.Control
                  name="email"
                  type="email"
                  value={values.email}
                  onBlur={handleBlur}
                  onChange={handleChange}
                  placeholder="Introdu email"
                  isValid={touched.email && !errors.email}
                  isInvalid={touched.email && !!errors.email}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.email}
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="formBasicPassword">
                <Form.Label>Parolă</Form.Label>
                <Form.Control
                  name="password"
                  type="password"
                  value={values.password}
                  placeholder="Introdu Parola"
                  onChange={handleChange}
                  onBlur={handleBlur}
                  isValid={touched.password && !errors.password}
                  isInvalid={touched.password && !!errors.password}
                />
                <Form.Control.Feedback type="invalid">
                  {errors.password}
                </Form.Control.Feedback>
              </Form.Group>
              <Form.Group controlId="formBasicCheckbox">
                <Form.Label>Tip cont</Form.Label>
                <Form.Control
                  as="select"
                  name="type"
                  value={type}
                  onChange={(e) => {
                    setType(e.target.value);
                  }}
                  onBlur={handleBlur}
                >
                  <option value="student">Student</option>
                  <option value="teacher">Profesor</option>
                </Form.Control>
              </Form.Group>
              <Form.Row className="justify-content-center">
                {/* <Link to="/" className="loginLink"> */}
                <LoginBtn variant="primary" type="submit">
                  <p>Login</p>
                </LoginBtn>
                {/* </Link> */}
              </Form.Row>

              <div class="sau">
                <p>
                  <span>sau</span>
                </p>
              </div>

              <Form.Row className="justify-content-center">
                <RegisterBtn variant="secondary" type="submit">
                  Register
                </RegisterBtn>
              </Form.Row>
            </Form>
          )}
        </Formik>
      </FormCard>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  // .loginLink {
  //   text-decoration: none;
  // }
  a {
    text-decoration: none;
  }
`;

const FormCard = styled(Card)`
  width: 30em;
  min-height 32em;

  .sau {
    margin-top: 2em;
    margin-bottom: 1em;
    p {
      
      
      width: 100%; 
      text-align: center; 
      border-bottom: 1px solid ${theme.Grey2}; 
      line-height: 0.1em;
      margin: 10px 0 20px; 
      
   } 
   
   p span { 
       background:#fff; 
       padding:0 10px; 
       font-weight: bold;
       color: ${theme.Grey1}
   }
  }

`;

const LoginBtn = styled(Button)`
  background: ${theme.mainBlue};
  color: white;
  margin-top: 2em;
  font-size: 1rem;
  font-weight: bold;
  height: 2em;
  width: 20em;
  border: none;

  &:hover {
    background: ${theme.mainBlue};
    color: white;
  }
`;

const RegisterBtn = styled(Button)`
  background: white;
  color: ${theme.mainBlue};
  margin-top: 2em;
  font-size: 1rem;
  font-weight: bold;
  height: 2em;
  width: 20em;
  border: 1px solid ${theme.Grey1};
  box-sizing: border-box;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.08);
  border-radius: 5px;

  &:hover {
    background: white;
    color: ${theme.mainBlue};
  }
`;

export default Login;
