import { useContext, useState } from "react";
import styled from "styled-components";
import theme from "../Assets/theme";
import { AppContext } from "../context";
import { Card, Button } from "../components/defaultComponents";

function EvaluateTest() {
  const { setShowNavbar } = useContext(AppContext);
  const [feedback, setFeedback] = useState("");
  console.log("esti in pagina de evaluare boss");
  setShowNavbar(false);
  return (
    <Wrapper>
      <EditorCard></EditorCard>
      <ConsoleCard></ConsoleCard>
      <FeedbackCard>
        <Banner>
          <p>Comentarii È™i feedback</p>
        </Banner>
        <TextArea
          value={feedback}
          onChange={(e) => setFeedback(e.target.value)}
        ></TextArea>
      </FeedbackCard>
      <GradeCard></GradeCard>
      <SubmitBtn>
        <div>Trimite</div>
      </SubmitBtn>
    </Wrapper>
  );
}

const EditorCard = styled(Card)`
  // grid-area: B;
  // margin-right: 2em;
  // margin-top: 3em;
  // align-items: streched
  // justify-items : streched;
`;

const ConsoleCard = styled(Card)`
  // grid-area: C;
  // margin-right: 2em;
  // position: relative;
  // display: flex;
  // flex-direction: row;
  // justify-content: space-between;
  // overflow: auto;
  // flex-wrap: wrap;
`;

const FeedbackCard = styled(Card)`
  // grid-area: C;
  // margin-right: 2em;
  // position: relative;
  // display: flex;
  // flex-direction: row;
  // justify-content: space-between;
  // overflow: auto;
  // flex-wrap: wrap;
  padding: 0;
`;

const Banner = styled.div`
  width: 100%;
  background-color: ${theme.Grey1};
  border-radius: 10px 10px 0 0;
  color: ${theme.mainBlack};
`;

const TextArea = styled.textarea`
  border: none;
  resize: none;
  outline: none;
  color: red;
`;

const SubmitBtn = styled(Button)`
  // height: 50px;
  // width: 150px;
  // grid-are: E;
  // justify-self: end;
  // align-self: center;
  // margin-right: 2em;
`;

const GradeCard = styled(Card)`
  // grid-area: C;
  // margin-right: 2em;
  // position: relative;
  // display: flex;
  // flex-direction: row;
  // justify-content: space-between;
  // overflow: auto;
  // flex-wrap: wrap;
`;

const Wrapper = styled.div`
  display: flex;
  height: 90vh;
  align-items: center;
  justify-content: center;
  font-size: 3rem;
`;

export default EvaluateTest;
