import React, {useState, useEffect} from 'react';
import axios from "axios";
const rootURL = "http://localhost:5000/api";

const AppContext = React.createContext();

const  getStudentTests = async (student) => {
    setIsLoading(true);
    const response  = await axios(`${rootURL}/students/${student.id}/due`).catch((err) => console.log(err));
}

const AppProvider = ({children}) => {
    const [isLoading, setIsLoading] = useState(false);
    const [tests, setTests] = useState("tests");
    const [grades, setGrades] = useState("grades");
    return <AppContext.Provider value={{tests, grades, isLoading}}>{children} </AppContext.Provider>
}

export {AppContext, AppProvider}
