import { Link } from "react-router-dom";
import React, { useEffect, useState } from "react";
import { AppContext } from "../context/context";
import { Card, Button } from "../components/defaultComponents";
import styled from "styled-components";
import { Form, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import theme from "../Assets/theme";

function Login() {
  const { setShowNavbar, setShowHeader } = React.useContext(AppContext);
  setShowNavbar(false);
  setShowHeader(false);

  return (
    <Wrapper>
      <FormCard>
        <Form>
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Adresă de email</Form.Label>
            <Form.Control type="email" placeholder="Introdu email" />
          </Form.Group>
          <Form.Group controlId="formBasicPassword">
            <Form.Label>Parolă</Form.Label>
            <Form.Control type="password" placeholder="Introdu Parola" />
          </Form.Group>
          <Form.Group controlId="formBasicCheckbox">
            <Form.Label>Tip cont</Form.Label>
            <Form.Control as="select">
              <option>Student</option>
              <option>Profesor</option>
            </Form.Control>
          </Form.Group>
          <Form.Row className="justify-content-center">
            <LoginBtn variant="primary" type="submit">
              Login
            </LoginBtn>
          </Form.Row>
          <div class="sau">
            <p>
              <span>sau</span>
            </p>
          </div>
        </Form>
        {/* <h1>Login Page</h1>
        <Link to="/" className="btn">
          Login
        </Link>
        <Link to="/register" className="btn">
          Creează cont
        </Link> */}
      </FormCard>
    </Wrapper>
  );
}

const Wrapper = styled.div`
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const FormCard = styled(Card)`
  width: 30em;
  height 30em;

  .sau {
    p {
      color: ${theme.Grey1}
      width: 100%; 
      text-align: center; 
      border-bottom: 1px solid ${theme.mainGrey}; 
      line-height: 0.1em;
      margin: 10px 0 20px; 
      
   } 
   
   p span { 
       background:#fff; 
       padding:0 10px; 
   }
  }

`;

const LoginBtn = styled(Button)`
  background: ${theme.mainBlue};
  color: white;
  margin-top: 2em;
  font-size: 1rem;
  font-weight: 400;
  height: 2em;
  width: 20em;
  border: none;

  &:hover {
    background: ${theme.mainBlue};
    color: white;
    font-weight: normal;
  }
`;

export default Login;
