import styled from "styled-components";
import {Form, Col} from "react-bootstrap"
import { AppContext } from "../context/context";
import React, {useState, useEffect} from "react";
import {Button} from "../components/defaultComponents"
import theme from "../Assets/theme";
import { Loader } from "../components";
import axios from "axios";
const rootURL = "http://localhost:5000/api";

function AddTest() {
  const {judge0Languges, isLoading, setIsLoading} = React.useContext(AppContext)
  const [subjects, setSubjects] = useState([]);
  const [groups, setGroups] = useState();
  const [areGroupsLoading, setAreGroupsLoading] = useState(true);

  //form controlled input fields
  const [selectedSubject, setSelectedSubject] = useState(-1)
  const [selectedGroup, setSelectedGroup] = useState();
  const [code, setCode] = useState();
  const [expectedAnswer, setExpectedAnswer] = useState();

  //onMount
  useEffect(() => {
    getSubjects();
  },[])

  function subjectChanged (e){  
    let id = e.target.value;
    setSelectedSubject(e.target.value)
    if(id > 0) {        
        getGroups(e.target.value)
    }  
    
  }

  

  async function getGroups(subjectId) {
    console.log("getting groups from db...");
    setAreGroupsLoading(true)
    const response  = await axios({
        method : "get",
        url : `${rootURL}/subjects/${subjectId}/groups`,
        
    })
    .catch((err) => console.log(err));

    if(response) {
        console.log(response.data)  
        setGroups(response.data);      
    }
    setAreGroupsLoading(false);
    
  }

  async function getSubjects() {
    console.log("getting subjects from db...");
    setIsLoading(true); 
    const response  = await axios({
        method : "get",
        url : `${rootURL}/teachers/1/subjects`,
        
    })
    .catch((err) => console.log(err));

    if(response) {
        console.log(response.data)  
        setSubjects(response.data);          
    }
    setIsLoading(false);
  }
  
  if(isLoading){
    console.log("is loading..")
    return(<Loader></Loader>)
  }
  
  return (
    <Wrapper>    
      <div>
        <FormTitle>Adaugă Test</FormTitle>
        <Form>
          <Form.Row>
            <Col></Col>
            <Col></Col>
          </Form.Row>
          <Form.Group controlId="subjects">
            <Form.Label>Materie</Form.Label>
            <Form.Control 
              as="select" 
              onChange={(e => subjectChanged(e))}>
              <option value={-1}>Selectează</option>
              {subjects.map((subject) => (
                  <option 
                  value={subject.id} 
                  id={subject.id}>
                      {subject.descriere}
                  </option>
                ))}
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="StudentGroups">
            <Form.Label>Grupa care va susține testul</Form.Label>
            <Form.Control as="select">
             {areGroupsLoading && selectedSubject == -1 && (
               <option>nicio grupa pentru materia selectată</option>
             )}
             {!areGroupsLoading && groups && selectedSubject > 0 &&(
               groups.map((item) => (
                 <option value={item.grupa}>{item.grupa}</option>
               ))
             )}
            </Form.Control>
          </Form.Group>        

         
          <Form.Group controlId="programmingLanguages">
            <Form.Label>Limbaj de programare</Form.Label>
            <Form.Control as="select" >
              {judge0Languges.map((language) => (
                <option value={language.name} key={language.id}>{language.name}</option>
              ))}
            </Form.Control>
          </Form.Group>         
          <Form.Group controlId="Problem">
            <Form.Label>Problemă</Form.Label>
            <Form.Control as="textarea" rows={3} />
          </Form.Group>
          <Form.Row>
            <Col><Form.Control placeholder="Rezultat așteptat" /></Col>
            <Col><Form.Control placeholder="Rezultat așteptat" /></Col>
          </Form.Row>
          
          <Form.Row className="justify-content-center">
          <SendBtn primary>Trimite</SendBtn>
          </Form.Row>
          
        </Form>
      </div>
    </Wrapper>
  );
}

const SendBtn = styled(Button)`
background: ${theme.mainBlue};
color: white;
margin-top: 2em;
&:hover {
  background: ${theme.mainBlue};;
  color: white;
  font-weight: normal;
}

`;

const FormTitle = styled.p`
  display: flex;
  justify-content: center;
  font-size: 2em;
`;
const Wrapper = styled.div`
display: flex;
justify-content: center;
align-items: center;
margin-left: 20vw;
height: 92vh; 

div {
  width : 30vw;
}

`;

export default AddTest;
